<form role="form"
          method="POST"
          action="{{{ URL::to('/users/login') }}}"
          accept-charset="UTF-8"
          class="navbar-form navbar-right">
           <input type="hidden" name="_token" value="{{{ Session::getToken() }}}">
            <div class="form-group">
              <input class="form-control" tabindex="1" placeholder="{{{ Lang::get('confide::confide.username_e_mail') }}}" type="text" name="email" id="email" value="{{{ Input::old('email') }}}" style="cursor: auto; background-image: url(data:image/png;base64,); background-attachment: scroll; background-position: 100% 50%; background-repeat: no-repeat;">
            </div>
            <div class="form-group">
              <input class="form-control" tabindex="2" placeholder="{{{ Lang::get('confide::confide.password') }}}" type="password" name="password" id="password" style="background-image: url(data:image/png;base64,); background-attachment: scroll; background-position: 100% 50%; background-repeat: no-repeat;">
            </div>
            <button type="submit" class="btn btn-success">Sign in</button>
</form>
<ul class="nav navbar-nav pull-right">
  <li role="presentation">
    <a href="/users/create"><i class="glyphicon glyphicon-user">&nbsp;</i>sign up</a></li>
</ul>